import Foundation

class Day4Easy {
    func solve() {
        var table = [[Character]]()
        var values: [[Int]]
        let lines = input.split(separator: "\n")
        
        for line in lines {
            table.append(Array(line))
        }
        
        let height = table.count
        let width = table[0].count
        
        values = Array(repeating: Array(repeating: 0, count: width), count: height)
        
        for i in 0..<height {
            for j in 0..<width {
                if table[i][j] == "@" {
                    Helpers.safeAdd(1, to: &values, at: (i-1, j-1), with: (height, width))
                    Helpers.safeAdd(1, to: &values, at: (i-1, j), with: (height, width))
                    Helpers.safeAdd(1, to: &values, at: (i-1, j+1), with: (height, width))
                    Helpers.safeAdd(1, to: &values, at: (i, j-1), with: (height, width))
                    Helpers.safeAdd(1, to: &values, at: (i, j+1), with: (height, width))
                    Helpers.safeAdd(1, to: &values, at: (i+1, j-1), with: (height, width))
                    Helpers.safeAdd(1, to: &values, at: (i+1, j), with: (height, width))
                    Helpers.safeAdd(1, to: &values, at: (i+1, j+1), with: (height, width))
                }
            }
        }
        
        var result: Int = 0
        for i in 0..<height {
            for j in 0..<width {
                if values[i][j] < 4, table[i][j] == "@" {
                    result += 1
                }
            }
        }
        
        print(result)
    }
    
