import Foundation

class Day7Easy {
    func solve() {
        var splits = 0
        
        var grid: [[Character]] = input
            .split(separator: "\n")
            .map { Array($0) }
        
        for y in 0..<(grid.count - 1) {
            for x in 0..<grid[y].count {
                if grid[y][x] == "S" || grid[y][x] == "|" {
                    if grid[y+1][x] == "." {
                        grid[y+1][x] = "|"
                    } else if grid[y+1][x] == "^" {
                        splits += 1
                        grid[y+1][x-1] = "|"
                        grid[y+1][x+1] = "|"
                    }
                }
            }
        }
        
        print(splits)
    }
    
